import { useEffect, useRef, useState } from 'react'

export const Canvas = (props: any) => {
  const { width = '100%', height = '100%', backgroundColor = '#eee', ...rest } = props

  const canvasRef = useRef<HTMLCanvasElement>(null)
  const [context, setContext] = useState<CanvasRenderingContext2D>(null)

  const resizeCanvas = (context: CanvasRenderingContext2D) => {
    const canvas = context.canvas
    const { width, height } = canvas.getBoundingClientRect()

    if (canvas.width !== width || canvas.height !== height) {
      const { devicePixelRatio: ratio = 1 } = window
      canvas.width = width * ratio
      canvas.height = height * ratio
      context.scale(ratio, ratio)
      return true
    }
    return false
  }

  const draw = (frameCount: number) => {
    context.clearRect(0, 0, context.canvas.width, context.canvas.height)
    context.fillStyle = '#000000'
    context.beginPath()
    context.arc(50, 100, 20 * Math.sin(frameCount * 0.05) ** 2, 0, 2 * Math.PI)
    context.fill()

    // ctx.drawImage(
    //   'data:image/jpeg;base64,\n',
    //   0,
    //   0
    // )
  }

  useEffect(() => {
    //i.e. value other than null or undefined
    if (canvasRef.current) {
      const canvas = canvasRef.current
      //@ts-ignore
      const ctx: CanvasRenderingContext2D = canvas.getContext('2d')
      setContext(ctx)
    }
  }, [])

  useEffect(() => {
    let frameCount = 0
    let animationFrameId: number

    // Check if null context has been replaced on component mount
    if (context) {
      //Our draw came here
      const render = () => {
        frameCount++
        resizeCanvas(context)
        // Note: frameCount argument won't be needed
        draw(frameCount)
        animationFrameId = window.requestAnimationFrame(render)
      }
      render()
    }
    return () => {
      window.cancelAnimationFrame(animationFrameId)
    }
  }, [draw, context])

  return <canvas ref={canvasRef} {...rest} style={{ width, height, backgroundColor }} />
}
